// ðŸ›¡ï¸ WBS BPKH Enhanced - Windows Ready
// Copy seluruh kode ini ke src/App.jsx

import React, { useState, useEffect } from 'react';
import { 
  Shield, 
  AlertTriangle, 
  FileText, 
  Phone, 
  Mail, 
  MessageSquare, 
  Eye, 
  EyeOff, 
  ChevronRight, 
  BarChart3, 
  Users, 
  Clock, 
  CheckCircle, 
  Star, 
  Globe, 
  Lock, 
  Smartphone, 
  Headphones,
  Upload,
  Send,
  ArrowRight,
  TrendingUp,
  Activity,
  AlertCircle,
  Settings,
  Download,
  Filter,
  Search,
  MessageCircle,
  Bot,
  X,
  Minimize2,
  Maximize2,
  ThumbsUp,
  ThumbsDown,
  Copy,
  Paperclip
} from 'lucide-react';

const WhistleblowingSystem = () => {
  const [currentView, setCurrentView] = useState('home');
  const [language, setLanguage] = useState('id');
  const [isAnonymous, setIsAnonymous] = useState(true);
  const [chatbotOpen, setChatbotOpen] = useState(false);
  const [chatbotMinimized, setChatbotMinimized] = useState(false);
  const [chatMessages, setChatMessages] = useState([
    {
      id: 1,
      type: 'bot',
      message: 'Selamat datang di Asisten WBS BPKH! ðŸ¤–\n\nSaya siap membantu Anda secara anonim 24/7. Bagaimana saya bisa membantu?',
      timestamp: new Date().toISOString(),
      options: [
        'Cara melaporkan pelanggaran',
        'Jenis-jenis pelanggaran',
        'Keamanan dan anonimitas',
        'Status laporan saya'
      ]
    }
  ]);
  const [chatInput, setChatInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [anonymousId] = useState('ANON-' + Math.random().toString(36).substr(2, 9).toUpperCase());
  const [reportForm, setReportForm] = useState({
    category: '',
    incident_type: '',
    description: '',
    location: '',
    date: '',
    evidence: null,
    urgency: 'medium'
  });
  const [caseStats, setCaseStats] = useState({
    totalReports: 347,
    resolved: 289,
    inProgress: 42,
    pending: 16,
    avgResponseTime: '18 jam',
    satisfactionRate: 4.7
  });

  // Translations untuk multi-language
  const translations = {
    id: {
      title: 'Sistem Pelaporan Pelanggaran BPKH',
      subtitle: 'Laporkan dugaan pelanggaran dengan aman dan terpercaya',
      reportIncident: 'Laporkan Pelanggaran',
      trackReport: 'Lacak Laporan',
      dashboard: 'Dashboard',
      anonymous: 'Anonim',
      identified: 'Teridentifikasi',
      categories: {
        corruption: 'Korupsi & Suap',
        hajiFund: 'Penyalahgunaan Dana Haji',
        discrimination: 'Diskriminasi',
        fraud: 'Penipuan',
        ethics: 'Pelanggaran Etika',
        other: 'Lainnya'
      }
    },
    en: {
      title: 'BPKH Whistleblowing System',
      subtitle: 'Report suspected violations safely and confidentially',
      reportIncident: 'Report Incident',
      trackReport: 'Track Report',
      dashboard: 'Dashboard',
      anonymous: 'Anonymous',
      identified: 'Identified',
      categories: {
        corruption: 'Corruption & Bribery',
        hajiFund: 'Hajj Fund Misuse',
        discrimination: 'Discrimination',
        fraud: 'Fraud',
        ethics: 'Ethics Violation',
        other: 'Others'
      }
    },
    ar: {
      title: 'Ù†Ø¸Ø§Ù… Ø§Ù„Ø¥Ø¨Ù„Ø§Øº Ø¹Ù† Ø§Ù„Ù…Ø®Ø§Ù„ÙØ§Øª - Ù‡ÙŠØ¦Ø© Ø¥Ø¯Ø§Ø±Ø© Ø§Ù„Ù…Ø§Ù„ÙŠØ© Ù„Ù„Ø­Ø¬',
      subtitle: 'Ø£Ø¨Ù„Øº Ø¹Ù† Ø§Ù„Ù…Ø®Ø§Ù„ÙØ§Øª Ø§Ù„Ù…Ø´ØªØ¨Ù‡ ÙÙŠÙ‡Ø§ Ø¨Ø£Ù…Ø§Ù† ÙˆØ³Ø±ÙŠØ©',
      reportIncident: 'Ø£Ø¨Ù„Øº Ø¹Ù† Ø­Ø§Ø¯Ø«Ø©',
      trackReport: 'ØªØªØ¨Ø¹ Ø§Ù„ØªÙ‚Ø±ÙŠØ±',
      dashboard: 'Ù„ÙˆØ­Ø© Ø§Ù„ØªØ­ÙƒÙ…',
      anonymous: 'Ù…Ø¬Ù‡ÙˆÙ„',
      identified: 'Ù…Ø¹Ø±Ù',
      categories: {
        corruption: 'Ø§Ù„ÙØ³Ø§Ø¯ ÙˆØ§Ù„Ø±Ø´ÙˆØ©',
        hajiFund: 'Ø¥Ø³Ø§Ø¡Ø© Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø£Ù…ÙˆØ§Ù„ Ø§Ù„Ø­Ø¬',
        discrimination: 'Ø§Ù„ØªÙ…ÙŠÙŠØ²',
        fraud: 'Ø§Ù„Ø§Ø­ØªÙŠØ§Ù„',
        ethics: 'Ø§Ù†ØªÙ‡Ø§Ùƒ Ø§Ù„Ø£Ø®Ù„Ø§Ù‚',
        other: 'Ø£Ø®Ø±Ù‰'
      }
    }
  };

  const t = translations[language];

  // Chatbot AI Responses - Enhanced untuk Windows
  const getBotResponse = (userMessage) => {
    const message = userMessage.toLowerCase();
    
    if (message.includes('cara melaporkan') || message.includes('how to report')) {
      return {
        message: 'ðŸ“‹ Ada beberapa cara untuk melaporkan pelanggaran:\n\n1. ðŸ“ Form Online - Isi formulir pelaporan di website ini\n2. â˜Žï¸ Hotline 24/7 - Hubungi 0800-1-BPKH (2754)\n3. ðŸ’¬ Chat dengan saya secara anonim\n4. ðŸ“§ Email terenkripsi ke wbs@bpkh.go.id\n\nðŸ”’ Semuanya dijamin aman dan rahasia dengan enkripsi tingkat militer!',
        options: ['Mulai membuat laporan', 'Penjelasan keamanan sistem', 'Jenis pelanggaran apa saja?']
      };
    }
    
    if (message.includes('jenis pelanggaran') || message.includes('kategori')) {
      return {
        message: 'ðŸŽ¯ Berikut jenis pelanggaran yang dapat dilaporkan di BPKH:\n\nðŸ¤ Korupsi & Suap\nðŸ•‹ Penyalahgunaan Dana Haji\nâš–ï¸ Diskriminasi & Harassment\nðŸ’° Penipuan & Fraud\nðŸ“‹ Pelanggaran Etika\nðŸ¢ Konflik Kepentingan\nðŸ“Š Manipulasi Data Keuangan\nðŸ”’ Pelanggaran Keamanan Data\n\nâ“ Ada yang ingin Anda laporkan?',
        options: ['Ya, saya ingin melaporkan', 'Butuh penjelasan lebih detail', 'Bagaimana jika saya ragu?']
      };
    }
    
    if (message.includes('keamanan') || message.includes('anonim') || message.includes('rahasia')) {
      return {
        message: 'ðŸ›¡ï¸ Keamanan dan anonimitas adalah prioritas utama BPKH:\n\nðŸ” Enkripsi AES 2048-bit untuk semua data\nðŸ‘¤ Zero IP logging - kami TIDAK menyimpan alamat IP\nðŸ›¡ï¸ No metadata collection\nðŸ†” Anonymous ID Anda: ' + anonymousId + '\nðŸš« Anti-retaliation policy ketat\nâš–ï¸ Perlindungan hukum penuh UU No. 31/1999\nðŸ”’ Server aman ISO 27001 certified\n\nâœ… Anda 100% aman melaporkan tanpa takut!',
        options: ['Saya siap melaporkan', 'Apa itu anti-retaliation?', 'Bisakah dideteksi identitas saya?']
      };
    }
    
    if (message.includes('status laporan') || message.includes('track') || message.includes('lacak')) {
      return {
        message: 'ðŸ” Untuk melacak status laporan Anda:\n\n1. ðŸ“‹ Gunakan ID laporan yang diberikan saat submit\n2. ðŸŒ Masukkan di halaman "Lacak Laporan"\n3. â±ï¸ Lihat timeline progress real-time\n4. ðŸ“± Dapatkan notifikasi update status\n\nðŸ’¡ Jika Anda belum punya ID laporan, saya bisa bantu membuatkan laporan baru.',
        options: ['Lacak laporan sekarang', 'Buat laporan baru', 'Saya lupa ID laporan']
      };
    }
    
    if (message.includes('mulai') || message.includes('buat laporan') || message.includes('melaporkan')) {
      return {
        message: 'ðŸš€ Excellent! Saya akan memandu Anda membuat laporan step-by-step.\n\nUntuk memulai, silakan pilih kategori pelanggaran yang paling sesuai dengan situasi Anda:',
        options: ['Korupsi & Suap', 'Penyalahgunaan Dana Haji', 'Diskriminasi', 'Penipuan', 'Pelanggaran Etika', 'Lainnya'],
        action: 'start_report'
      };
    }
    
    if (message.includes('korupsi') || message.includes('suap')) {
      return {
        message: 'âš ï¸ Anda memilih kategori KORUPSI & SUAP.\n\nIni adalah pelanggaran serius yang meliputi:\nâ€¢ ðŸ’° Menerima atau memberi suap\nâ€¢ ðŸ›ï¸ Penyalahgunaan wewenang\nâ€¢ ðŸŽ Gratifikasi tidak sah\nâ€¢ ðŸ“ˆ Mark-up anggaran/tender\nâ€¢ ðŸ¤ Kolusi dalam pengadaan\n\nðŸ”¥ Kategori ini mendapat prioritas investigasi TINGGI!\n\nApakah Anda siap melanjutkan ke form pelaporan?',
        options: ['Ya, lanjut ke form', 'Butuh informasi lebih detail', 'Saya masih ragu'],
        action: 'goto_report'
      };
    }
    
    if (message.includes('dana haji') || message.includes('hajj fund')) {
      return {
        message: 'ðŸ•‹ PENYALAHGUNAAN DANA HAJI - Pelanggaran sangat serius!\n\nKami memahami betapa sakralnya dana haji. Pelanggaran meliputi:\n\nðŸ’¸ Penggunaan dana di luar ketentuan syariah\nðŸ“Š Manipulasi laporan keuangan haji\nðŸŽ¯ Investasi tidak sesuai prinsip syariah\nðŸ‘¥ Nepotisme dalam tender haji\nðŸ“‹ Falsifikasi dokumen jamaah\nðŸ’¼ Markup biaya penyelenggaraan\n\nðŸ”¥ BPKH berkomitmen melindungi amanah jamaah!\n\nApakah Anda menyaksikan hal seperti ini?',
        options: ['Ya, saya ingin melaporkan', 'Butuh bukti apa saja?', 'Bagaimana prosesnya?']
      };
    }
    
    if (message.includes('bukti') || message.includes('evidence') || message.includes('dokumen')) {
      return {
        message: 'ðŸ“ Bukti yang dapat membantu investigasi BPKH:\n\nðŸ“„ Dokumen (PDF, Word, Excel, PowerPoint)\nðŸ“¸ Foto/Screenshot percakapan\nðŸŽ¥ Video rekaman (jika ada)\nðŸ’¬ Chat/Email/WhatsApp\nðŸ“Š Data transaksi/transfer\nðŸ“‹ Surat/memo internal\nðŸŽ§ Rekaman audio (jika legal)\nðŸ‘¥ Nama saksi (opsional)\n\nðŸ”’ Semua file akan dienkripsi dan aman!\nðŸ“ Upload maksimal 10MB per file\nðŸ›¡ï¸ Server secure dengan sertifikat ISO 27001',
        options: ['Saya punya bukti', 'Bagaimana jika tidak ada bukti?', 'Apakah bukti wajib?']
      };
    }
    
    if (message.includes('tidak ada bukti') || message.includes('tanpa bukti')) {
      return {
        message: 'âœ… Tidak masalah! Laporan tetap sangat berharga tanpa bukti fisik:\n\nðŸ‘ï¸ Kesaksian mata Anda sudah cukup penting\nðŸ“ Ceritakan detail yang Anda ingat\nðŸ“… Kapan kejadian berlangsung\nðŸ“ Di mana lokasi kejadian\nðŸ‘¥ Siapa saja yang terlibat\nðŸ’° Perkiraan nilai kerugian (jika ada)\nðŸŽ¯ Dampak yang ditimbulkan\n\nðŸ•µï¸ Tim investigasi profesional BPKH akan menindaklanjuti dengan teknik investigasi modern!\n\nâ±ï¸ Laporan akan diproses dalam 1x24 jam.',
        options: ['Lanjut buat laporan', 'Apa yang terjadi setelah lapor?', 'Berapa lama prosesnya?']
      };
    }
    
    if (message.includes('anti-retaliation') || message.includes('pembalasan')) {
      return {
        message: 'ðŸ›¡ï¸ ANTI-RETALIATION POLICY BPKH:\n\nðŸ“œ Berdasarkan UU No. 31/1999 tentang Pemberantasan Korupsi\nâš–ï¸ PP No. 43/2018 tentang Tata Cara Pelaksanaan Peran Serta Masyarakat\nðŸ”’ Perlindungan identitas whistleblower\nðŸš« DILARANG: pemecatan, demosi, mutasi tidak wajar\nðŸš« DILARANG: intimidasi, ancaman, tekanan\nðŸ’¼ Dukungan hukum gratis jika terjadi retaliation\nðŸ“ž Hotline khusus perlindungan saksi\n\nâš¡ Pelanggaran anti-retaliation = PIDANA!',
        options: ['Saya merasa aman sekarang', 'Bagaimana jika saya karyawan?', 'Prosedur jika ada retaliation?']
      };
    }
    
    // Default enhanced response
    return {
      message: 'ðŸ¤– Terima kasih telah menghubungi Asisten AI WBS BPKH!\n\nSaya di sini untuk membantu Anda dengan:\n\nðŸ“‹ Panduan membuat laporan pelanggaran\nðŸ”’ Informasi keamanan dan enkripsi sistem\nðŸ“Š Jenis-jenis pelanggaran yang dapat dilaporkan\nðŸ•µï¸ Proses investigasi dan tindak lanjut\nðŸ’¬ Konseling dan dukungan emosional\nâš–ï¸ Informasi perlindungan hukum\nðŸ“± Cara menggunakan semua fitur platform\n\nðŸ’¡ Saya tersedia 24/7 untuk membantu Anda!\n\nApa yang bisa saya bantu hari ini?',
      options: ['Cara melaporkan pelanggaran', 'Jenis-jenis pelanggaran', 'Keamanan dan anonimitas', 'Status laporan saya']
    };
  };

  const sendChatMessage = (message) => {
    if (!message.trim()) return;
    
    const userMessage = {
      id: Date.now(),
      type: 'user',
      message: message,
      timestamp: new Date().toISOString()
    };
    
